def find_start_recursive(datastream, i):
    if i == len(datastream):
        return -1
    if len(set(datastream[i-4:i])) == 4:
        return i
    else:
        return find_start(datastream, i+1)
      
def find_start_iterative(datastream):
    for i in range(3, len(datastream)):
        if len(set(datastream[i-4:i])) == 4:
            return i
    return -1

def find_message_iterative(datastream):
    for i in range(14, len(datastream)):
        if len(set(datastream[i-14:i])) == 14:
            return i
    return -1
  
